<?php/*http://BearDev.com */// Check to ensure this file is included in Joomla!defined('_JEXEC') or die( 'Restricted access' );class JElementModseas extends JElement{	/**	 * Element name	 *	 * @access	protected	 * @var		string	 */	var	$_name = 'Season';	function fetchElement($name, $value, &$node, $control_name)	{		global $mainframe;		$db			=& JFactory::getDBO();		$doc 		=& JFactory::getDocument();		$template 	= $mainframe->getTemplate();		$fieldName	= $control_name.'['.$name.']';				$query = "SELECT CONCAT(t.name,' ',s.s_name) as name,s.s_id as id FROM #__bl_tournament as t, #__bl_seasons as s WHERE s.t_id = t.id";		$db->setQuery($query);			$rows = $db->loadObjectList();								$html = JHTML::_('select.genericlist',   $rows, 'params[season_id]', 'class="inputbox" size="1"', 'id', 'name', $value);		//$html .= "\n".'<input type="hidden" id="'.$name.'_id" name="'.$fieldName.'" value="'.$value.'" />';				return $html;	}}