<?php/*http://BearDev.com */// Check to ensure this file is included in Joomla!defined('_JEXEC') or die( 'Restricted access' );jimport('joomla.form.formfield');class JFormFieldModseas extends JFormField{	/**	 * Element name	 *	 * @access	protected	 * @var		string	 */	protected $type = 'Season';	protected function getInput()	{		$db			=& JFactory::getDBO();		$doc 		=& JFactory::getDocument();				//$fieldName	= 'season['.$name.']';				$query = "SELECT CONCAT(t.name,' ',s.s_name) as name,s.s_id as id FROM #__bl_tournament as t, #__bl_seasons as s WHERE s.t_id = t.id";		$db->setQuery($query);			$rows = $db->loadObjectList();								$html = JHTML::_('select.genericlist',   $rows, 'jform[params][season_id]', 'class="inputbox" size="1"', 'id', 'name', $this->value);		//$html .= "\n".'<input type="hidden" id="'.$name.'_id" name="'.$fieldName.'" value="'.$this->value.'" />';				return $html;	}}