<?php/*http://BearDev.com */// Check to ensure this file is included in Joomla!defined('_JEXEC') or die( 'Restricted access' );jimport('joomla.form.formfield');class JFormFieldModteam extends JFormField{	/**	 * Element name	 *	 * @access	protected	 * @var		string	 */	 protected $type = 'team';	protected function getInput(){		$db			=& JFactory::getDBO();		$doc 		=& JFactory::getDocument();				//$fieldName	= 'team['.$name.']';				$query = "SELECT t_name as name,id FROM #__bl_teams";		$db->setQuery($query);			$rows = $db->loadObjectList();		$pos[] = JHTML::_('select.option',  0, JText::_('Select Team'), 'id', 'name' ); 		$teams = array_merge($pos,$rows);						$html = JHTML::_('select.genericlist',   $teams, 'jform[params][team_id]', 'class="inputbox" size="1"', 'id', 'name', $this->value);		//$html .= "\n".'<input type="hidden" id="'.$name.'_id" name="'.$fieldName.'" value="'.$this->value.'" />';				return $html;	}}