<?phpdefined('_JEXEC') or die( 'Restricted access' );jimport('joomla.application.component.controller');$mainframe = JFactory::getApplication();$doc =& JFactory::getDocument();$doc->addCustomTag( '<link rel="stylesheet" type="text/css"  href="components/com_joomsport/css/admin_bl.css" />' );$doc->addCustomTag( '<link rel="stylesheet" type="text/css"  href="components/com_joomsport/css/joomsport.css" />' );require_once('includes/func.php');if(getVer() == 1.6){	$doc->addCustomTag( '<script type="text/javascript" src="components/com_joomsport/includes/slimbox/js/slimbox.js"></script>' );}else{	JHTML::_('behavior.mootools');	$doc->addCustomTag( '<script type="text/javascript" src="components/com_joomsport/includes/slimbox/js15/slimbox.js"></script>' );}$doc->addCustomTag( '<link rel="stylesheet" type="text/css"  href="components/com_joomsport/includes/slimbox/css/slimbox.css" />' );class BLeagueController extends JController{	/**	 * Method to display a view	 *	 * @access	public	 * @since	1.5	 */	function display()	{		$view = JRequest::getCmd( 'view' );		if(!$view) {			$view = 'ltable';		}		JRequest::setVar( 'view', $view );		$cal = JRequest::getCmd( 'layout' );		if($cal == 'calendar'){			JRequest::setVar( 'view', 'calendar' );			JRequest::setVar( 'layout', 'default' );		}		parent::display();	}		function team()	{		JRequest::setVar( 'view', 'blteam' );		parent::display();	}	function view_match()	{		JRequest::setVar( 'view', 'match' );		parent::display();	}	function player()	{		JRequest::setVar( 'view', 'player' );		parent::display();	}	function calendar()	{		JRequest::setVar( 'view', 'calendar' );						parent::display();	}	function get_js_version(){		$js_version = '1.1.3.3';		echo $js_version;		exit();			}}	