<?php/** http://www.BearDev.com */// no direct accessdefined('_JEXEC') or die('Restricted access');//require_once(JPATH_COMPONENT.DS.'controller.php');// Require specific controller if requestedif($controller = JRequest::getWord('controller')) {		$path = JPATH_COMPONENT.DS.'controllers'.DS.$controller.'.php';	if (file_exists($path) && $controller) {		require_once $path;	} else {		$controller = '';		require_once(JPATH_COMPONENT.DS.'controller.php');	}}else {		$controller = '';		require_once(JPATH_COMPONENT.DS.'controller.php');	}// Create the controller$classname	= 'BLeagueController'.ucfirst($controller);$controller = new $classname( );$seasid = JRequest::getVar( 'seasid', 0, '', 'int' );$sid = JRequest::getVar( 'sid', 0, '', 'int' );if($seasid && !$sid){	JRequest::setVar( 'sid', $seasid );}// Perform the Request task$controller->execute(JRequest::getVar('task', null, 'default', 'cmd'));// Redirect if set by the controller$controller->redirect();