 = $this->players[$i];		?>		<tr>			<?php				for ($j=0;$j<count($players);$j++){					echo "<td>".$players[$j]."</td>";				}			?>					</tr>		<?php	}	?>	</tbody><?php } ?>	</table></div><?phpif(count($this->photos)){echo '<div id="etab_photos_div" class="tabdiv" style="display:none;">';echo "<table class='jsnoborders'><tr><td>";	for($i=0;$i<count($this->photos);$i++){		$photo = $this->photos[$i];			?>		<div style="float:left; padding:10px; height:120px;">			<a rel="lightbox-imgsport" title="<?php echo htmlspecialchars($photo->name)?>" href="<?php echo getImgPop($photo->filename)?>" class="team-images"><img src="<?php echo (JURI::base().'media/bearleague/'.$photo->filename);?>"  height="100" class="allimages" alt="<?php echo htmlspecialchars($photo->name)?>" title="<?php echo htmlspecialchars($photo->name)?>" /></a>		</div>	<?php	}echo "</td></tr></table></div>";	}?><div align="right" style="padding-bottom:30px;"><div style="width:100px;float:right;text-align:right"><a href="javascript:void(0);" onclick="history.back(-1);"><?php echo $this->bl_lang['BL_BACK']?></a></div></div><script type="text/javascript" src="<?php echo JURI::base()?>components/com_joomsport/js/grid.js"></script><script type="text/javascript">new Grid($('e_table')); </script>