<?php // no direct accessdefined('_JEXEC') or die('Restricted access'); jimport('joomla.html.pane');$tabs = &JPane::getInstance('tabs', array('allowAllClose' => true)); 	if(isset($this->message)){		$this->display('message');	}?><?php if ( $this->params->def( 'show_page_title', 1 ) ) : ?><div class="componentheading<?php echo $this->params->get( 'pageclass_sfx' ); ?>">	<?php echo $this->escape($this->params->get('page_title')); ?></div><?php endif; ?><?php//echo '<div id="d_name">'.$this->md_title."</div>";?><table style="margin-top:15px;" border="0" cellpadding="5" cellspacing="0" width="100%">	<?php		for($i=0;$i<count($this->match);$i++){			$match = $this->match[$i];		?>		<tr>			<td class="team_thome"><?php echo $match->home?></td>			<td class="match_result" nowrap="nowrap">				<?php 				if($match->m_played == 1){					echo $match->score1?> : <?php echo $match->score2; if($this->enbl_extra && $match->is_extra){ echo " (".$this->bl_lang['BL_RES_EXTRA'].")";}				}else{					echo " - : - ";				}				?>			</td>			<td class="team_taway"><?php echo $match->away?></td>			<td class="match_details"> 				<?php				if($match->m_played == 1){?>				<a href="<?php echo JRoute::_('index.php?option=com_joomsport&task=view_match&id='.$match->id).'">'.$this->bl_lang['BL_LINK_DETAILMATCH'].' '?></a>				<?php } ?>			</td>   		</tr>				<?php		}	?>	</table><div align="right"><div style="width:100px;float:right;text-align:right"><a href="javascript:void(0);" onclick="history.back(-1);"><?php echo $this->bl_lang["BL_BACK"]?></a></div></div>